import UIKit
import XCTest

class ImageHelperTests: XCTestCase {
    func testImageWithBase64EncodedString() {
       let imageBase64EncodedString: String = ""
        XCTAssertNil(ImageHelper.imageWithBase64EncodedString(""))
        XCTAssertNotNil(ImageHelper.imageWithBase64EncodedString(imageBase64EncodedString))
    }
}
